INSERT INTO MC_BOARD_COLUMN (ORDER_NUM,COL_CODE,COL_KOR,COL_HELPER, COL_EDIT) VALUES ('1','rn','번호','리스트 페이지에서 보여주기만 가능하며, 수정은 불가능합니다.', 'N');
INSERT INTO MC_BOARD_COLUMN (ORDER_NUM,COL_CODE,COL_KOR,COL_HELPER, COL_EDIT) VALUES ('3','title','제목','제목을 입력하는 필드이며, 게시판의 필수항목입니다.', 'Y');
INSERT INTO MC_BOARD_COLUMN (ORDER_NUM,COL_CODE,COL_KOR,COL_HELPER, COL_EDIT) VALUES ('4','conts','내용','게시물의 내용을 입력하는 필드로 에디터로 구성되어 있습니다.', 'Y');
INSERT INTO MC_BOARD_COLUMN (ORDER_NUM,COL_CODE,COL_KOR,COL_HELPER, COL_EDIT) VALUES ('5','ip','등록자아이피','게시물 등록자의 아이피가 자동으로 입력되며, 보여주기만 가능하고 수정은 불가합니다.', 'N');
INSERT INTO MC_BOARD_COLUMN (ORDER_NUM,COL_CODE,COL_KOR,COL_HELPER, COL_EDIT) VALUES ('6','view_cnt','조회수','게시물의 조회수를 보여주며, 수정은 불가능합니다.', 'N');
INSERT INTO MC_BOARD_COLUMN (ORDER_NUM,COL_CODE,COL_KOR,COL_HELPER, COL_EDIT) VALUES ('7','public_yn','공개/비공개','게시물의 공개 비공개를 선택 할 수 있도록 합니다.', 'Y');
INSERT INTO MC_BOARD_COLUMN (ORDER_NUM,COL_CODE,COL_KOR,COL_HELPER, COL_EDIT) VALUES ('8','notice_yn','공지','관리자 전용 기능으로 게시물을 최상단에 보이도록 하는 기능입니다.', 'Y');
INSERT INTO MC_BOARD_COLUMN (ORDER_NUM,COL_CODE,COL_KOR,COL_HELPER, COL_EDIT) VALUES ('9','cat_nm','카테고리','반드시 카테고리를 생성 후 사용해야 하며, 게시물의 카테고리를 지정해주는 기능을 합니다.', 'Y');
INSERT INTO MC_BOARD_COLUMN (ORDER_NUM,COL_CODE,COL_KOR,COL_HELPER, COL_EDIT) VALUES ('10','thumb','썸네일','내용 컬럼과 같이 사용해야 하며, 에디터에서 등록된 이미지를 썸네일로 보여주기만 가능합니다.', 'Y');
INSERT INTO MC_BOARD_COLUMN (ORDER_NUM,COL_CODE,COL_KOR,COL_HELPER, COL_EDIT) VALUES ('11','password','패스워드','게시물 등록시 비밀번호를 입력하며, 한번 등록되면 수정은 불가능합니다.', 'N');
INSERT INTO MC_BOARD_COLUMN (ORDER_NUM,COL_CODE,COL_KOR,COL_HELPER, COL_EDIT) VALUES ('12','sdate','시작일','날짜를 선택할 수 있도록 기능을 제공 합니다.', 'Y');
INSERT INTO MC_BOARD_COLUMN (ORDER_NUM,COL_CODE,COL_KOR,COL_HELPER, COL_EDIT) VALUES ('13','edate','종료일','날짜를 선택할 수 있도록 기능을 제공 합니다.', 'Y');
INSERT INTO MC_BOARD_COLUMN (ORDER_NUM,COL_CODE,COL_KOR,COL_HELPER, COL_EDIT) VALUES ('14','reg_dt','등록일','게시물의 등록일을 보여주며, 수정이 불가능합니다.', 'N');
INSERT INTO MC_BOARD_COLUMN (ORDER_NUM,COL_CODE,COL_KOR,COL_HELPER, COL_EDIT) VALUES ('15','reg_id','등록자아이디','게시물의 등록자 아이디가 보이며, 수정이 불가능합니다.', 'N');
