mapper.select:
SELECT PT1.* FROM ( SELECT ROW_NUMBER()OVER(ORDER BY a.SEQ DESC) RN, a.*, b.TITLE as TARGET_NM, c.TITLE as FORM_TITLE, d.TARGET_CNT FROM MC_MAIL a LEFT OUTER JOIN MC_MAIL_TARGET b ON a.TARGET_SEQ=b.SEQ LEFT OUTER JOIN MC_MAIL_FORM c ON a.FORM_SEQ=c.SEQ LEFT OUTER JOIN (SELECT P_SEQ, COUNT(1) as TARGET_CNT FROM MC_MAIL_QUEUE GROUP BY P_SEQ) d ON a.SEQ=d.P_SEQ
AND a.TITLE like '%'||#{keyword}||'%'
) PT1 WHERE RN BETWEEN NVL(#{rows, jdbcType=INTEGER},10) * (NVL(#{cpage, jdbcType=INTEGER},1) - 1) + 1 AND NVL(#{rows, jdbcType=INTEGER},10) * NVL(#{cpage, jdbcType=INTEGER},1)

mapper.update:
UPDATE MC_MAIL SET
	STATUS = 				#{status}
WHERE SEQ = #{seq}

INSERT INTO MC_MAIL_QUEUE(
	SEQ, P_SEQ, USER_NAME, USER_EMAIL, STATUS
)VALUES(
	SEQ_MC_MAIL_QUEUE.NEXTVAL, #{p_seq}, #{user_name}, #{user_email}, 'N'
)

UPDATE MC_MAIL_QUEUE SET
	STATUS = 'C'
WHERE SEQ = #{seq}

UPDATE MC_MAIL_QUEUE SET
	STATUS = 'S'
WHERE SEQ = #{seq}

UPDATE MC_MAIL_QUEUE SET
	STATUS = 'F'
WHERE SEQ = #{seq}

mapper.select:
SELECT PT1.* FROM ( SELECT ROW_NUMBER()OVER(ORDER BY SEQ DESC) RN, a.* FROM MC_MAIL_SMTP a
AND a.TITLE like '%'||#{keyword}||'%' AND a.AUTH_ID like CONCAT('%',#{keyword},'%') AND a.HOST like CONCAT('%',#{keyword},'%')
) PT1 WHERE RN BETWEEN NVL(#{rows, jdbcType=INTEGER},10) * (NVL(#{cpage, jdbcType=INTEGER},1) - 1) + 1 AND NVL(#{rows, jdbcType=INTEGER},10) * NVL(#{cpage, jdbcType=INTEGER},1)

SELECT PT1.* FROM ( SELECT ROW_NUMBER()OVER(ORDER BY SEQ DESC) RN, a.* FROM MC_MAIL_TARGET a
AND a.TITLE like '%'||#{keyword}||'%'
) PT1 WHERE RN BETWEEN NVL(#{rows, jdbcType=INTEGER},10) * (NVL(#{cpage, jdbcType=INTEGER},1) - 1) + 1 AND NVL(#{rows, jdbcType=INTEGER},10) * NVL(#{cpage, jdbcType=INTEGER},1)

SELECT PT1.* FROM ( SELECT ROW_NUMBER()OVER(ORDER BY SEQ DESC) RN, a.* FROM MC_MAIL_FORM a
AND a.TITLE like '%'||#{keyword}||'%'
) PT1 WHERE RN BETWEEN NVL(#{rows, jdbcType=INTEGER},10) * (NVL(#{cpage, jdbcType=INTEGER},1) - 1) + 1 AND NVL(#{rows, jdbcType=INTEGER},10) * NVL(#{cpage, jdbcType=INTEGER},1)

