mapper.select:
SELECT  
	sg.CMS_MENU_SEQ, sg.PARENT_MENU_SEQ, sg.TITLE, sg.MENU_ORDER, sg.SITE_ID, sg.MENU_URL, sg.TARGET_URL, sg.BLANK_YN, sg.CHILD_TYPE, sg.TOP_YN, sg.MENU_TYPE, sg.DEL_YN, sg.USE_YN
	, DATE_FORMAT(sg.REG_DT, '%Y-%m-%d') AS REG_DT,
	SUBSTR(SYS_CONNECT_BY_PATH_MENU(sg.CMS_MENU_SEQ, '>'), 2) AS PAGE_NAVI,
	sys_connect_by_path_menu_order(sg.CMS_MENU_SEQ, '_') AS order_menu_set
FROM    (
        SELECT  start_with_connect_by_memu() AS PARENT_MENU_SEQ, @level AS MENU_LEVEL
        FROM    (
                SELECT  @start_with := 0,
                        @id := @start_with,
                        @level := 0,
                        @rn := 0
                ) vars, MC_CMS_MENU
        ) sg2
JOIN    MC_CMS_MENU sg
ON      sg.CMS_MENU_SEQ = sg2.PARENT_MENU_SEQ
		AND sg.MENU_TYPE = '2'
WHERE sg.BOARD_SEQ = #{board_seq}
ORDER BY order_menu_set

mapper.update:
UPDATE MC_BOARD_LIST SET
	BOARD_TYPE = #{board_type},
	NAME = #{name}
WHERE
	SEQ = #{seq}

mapper.select:
SELECT A.* FROM ( SELECT CAST(@rownum:=@rownum+1 AS SIGNED) AS RN, TB.* , COALESCE(TB2.USER_PAGE_CNT, 0) AS USER_PAGE_CNT FROM ( SELECT BOARD_SEQ, BOARD_NM, BOARD_TYPE, USE_YN, EDITOR_YN, CCLNURI_YN, TAG_YN, CAT_YN, DATE_FORMAT(REG_DT, '%Y-%m-%d') AS REG_DT FROM MC_BOARD, (SELECT @rownum:=0) TMP WHERE DEL_YN='N' AND SITE_ID = IFNULL(#{site_id},1)
AND BOARD_TYPE = #{board_type}
AND BOARD_NM like CONCAT('%',#{keyword},'%')
AND USE_YN = #{use_yn}
AND DATE_FORMAT(REG_DT, '%Y-%m-%d') BETWEEN #{start_dt} AND #{end_dt}
ORDER BY REG_DT DESC ) TB LEFT OUTER JOIN (SELECT BOARD_SEQ, COUNT(1) AS USER_PAGE_CNT FROM MC_CMS_MENU WHERE DEL_YN = 'N' AND MENU_TYPE = '2' AND BOARD_SEQ IS NOT NULL GROUP BY BOARD_SEQ) TB2 ON TB.BOARD_SEQ = TB2.BOARD_SEQ ) A WHERE RN BETWEEN IFNULL(#{rows, jdbcType=INTEGER},10) * (IFNULL(#{cpage, jdbcType=INTEGER},1) - 1) + 1 AND IFNULL(#{rows, jdbcType=INTEGER},10) * IFNULL(#{cpage, jdbcType=INTEGER},1)

SELECT CEIL(COUNT(1) / IFNULL(#{rows, jdbcType=INTEGER} ,10)) TOTALPAGE, COUNT(1) TOTALCOUNT FROM MC_BOARD WHERE DEL_YN='N' AND SITE_ID = IFNULL(#{site_id},1)
AND BOARD_TYPE = #{board_type}
AND BOARD_NM like CONCAT('%',#{keyword},'%')
AND USE_YN = #{use_yn}
AND DATE_FORMAT(REG_DT, '%Y-%m-%d') BETWEEN #{start_dt} AND #{end_dt}

mapper.update:
UPDATE MC_BOARD_CAT SET
	DEL_YN = 'Y',
	DEL_DT=CURRENT_TIMESTAMP, 
	DEL_ID=#{session_member_id}, 
	DEL_NM=#{session_member_nm} 
WHERE

	BOARD_CAT_SEQ=#{board_cat_seq}


	BOARD_SEQ=#{board_seq}

UPDATE MC_BOARD_STATE SET
	DEL_YN = 'Y',
	DEL_DT=CURRENT_TIMESTAMP, 
	DEL_ID=#{session_member_id}, 
	DEL_NM=#{session_member_nm} 
WHERE

	BOARD_STATE_SEQ=#{board_state_seq}


	BOARD_SEQ=#{board_seq}
