mapper.select:
        SELECT
			COUNT(1) as CNT
		FROM MC_ANALYTICS
		WHERE 1=1


		AND YMD >= #{start_dt} AND YMD <= #{end_dt}



		AND SITE_ID = #{site_id}

SELECT COUNT(MEMBER_ID) AS CNT FROM( SELECT MAX(T.MEMBER_ID) MEMBER_ID FROM( SELECT MEMBER_ID, MEMBER_NAME, DATE_FORMAT(LOGIN_DATE, '%y%m%d') AS YMD FROM MC_STAFF_LOGIN_TRACKING
) T
WHERE YMD >= #{start_dt} AND YMD <= #{end_dt}
GROUP BY T.MEMBER_ID, T.YMD )T1
