	,TEMPLATE_TYPE = #{template_type}
	,PROGRAM_NM = #{program_nm}
	,MANAGE_URL = #{manage_url}
	,INNER_YN = #{inner_yn}

	,BOARD_SEQ = #{board_seq}


	,BOARD_SEQ = null

	,MENU_URL = #{menu_url}
