mapper.select:
SELECT
    ADD_YN, MOD_YN, VIEW_YN
FROM MC_MANAGE_AUTH_DETAIL
    WHERE CMS_MENU_SEQ = #{menu_seq}


	AND PARENT_SEQ = #{group_seq}


	AND PARENT_SEQ = (
                SELECT
                    MANAGE_SEQ
                FROM MC_GROUP
                WHERE GROUP_SEQ=#{group_seq}
            )

