mapper.update:
UPDATE MC_BOARD_LIST SET
	BOARD_TYPE = #{board_type},
	NAME = #{name}
WHERE
	SEQ = #{seq}

mapper.select:
SELECT A.* FROM ( SELECT ROWNUM RN, TB.*, COALESCE(TB2.USER_PAGE_CNT, 0) AS USER_PAGE_CNT FROM ( SELECT BOARD_SEQ, BOARD_NM, BOARD_TYPE, USE_YN, EDITOR_YN, CCLNURI_YN, TAG_YN, CAT_YN, TO_CHAR(REG_DT, 'YYYY-MM-DD') AS REG_DT FROM MC_BOARD WHERE DEL_YN='N' AND SITE_ID = NVL(#{site_id},1)
AND BOARD_TYPE = #{board_type}
AND BOARD_NM like '%'||#{keyword}||'%'
AND USE_YN = #{use_yn}
AND TO_CHAR(REG_DT, 'YYYY-MM-DD') BETWEEN #{start_dt} AND #{end_dt}
ORDER BY REG_DT DESC ) TB LEFT OUTER JOIN (SELECT BOARD_SEQ, COUNT(1) AS USER_PAGE_CNT FROM MC_CMS_MENU WHERE BOARD_SEQ IS NOT NULL GROUP BY BOARD_SEQ) TB2 ON TB.BOARD_SEQ = TB2.BOARD_SEQ ) A WHERE RN BETWEEN NVL(#{rows, jdbcType=INTEGER},10) * (NVL(#{cpage, jdbcType=INTEGER},1) - 1) + 1 AND NVL(#{rows, jdbcType=INTEGER},10) * NVL(#{cpage, jdbcType=INTEGER},1)

SELECT CEIL(COUNT(1) / NVL(#{rows, jdbcType=INTEGER} ,10)) TOTALPAGE, COUNT(1) TOTALCOUNT FROM MC_BOARD WHERE DEL_YN='N' AND SITE_ID = NVL(#{site_id},1)
AND BOARD_TYPE = #{board_type}
AND BOARD_NM like '%'||#{keyword}||'%'
AND USE_YN = #{use_yn}
AND TO_CHAR(REG_DT, 'YYYY-MM-DD') BETWEEN #{start_dt} AND #{end_dt}

mapper.update:
UPDATE MC_BOARD_CAT SET
	DEL_YN = 'Y',
	DEL_DT=SYSDATE, 
	DEL_ID=#{session_member_id}, 
	DEL_NM=#{session_member_nm} 
WHERE

	BOARD_CAT_SEQ=#{board_cat_seq}


	BOARD_SEQ=#{board_seq}

UPDATE MC_BOARD_STATE SET
	DEL_YN = 'Y',
	DEL_DT=SYSDATE, 
	DEL_ID=#{session_member_id}, 
	DEL_NM=#{session_member_nm} 
WHERE

	BOARD_STATE_SEQ=#{board_state_seq}


	BOARD_SEQ=#{board_seq}
